let dropdown = () => { let e = document.getElementById("dropdown"); "none" === e.style.display ? e.style.display = "block" : e.style.display = "none" },
    quartosAirBnb = () => { let e = new XMLHttpRequest;
        e.open("GET", "https://api.sheety.co/30b6e400-9023-4a15-8e6c-16aa4e3b1e72", !0), e.onreadystatechange = (() => { if (4 === e.readyState && 200 === e.status) { let t = JSON.parse(e.responseText);
                carregarQuartos(t) } }), e.send() },
    carregarQuartos = e => { for (let t = 0; t < e.length; t++) { let o = '<div class="card"><img class="foto" src=' + e[t].photo + '><div class="tipo-propriedade"><div><p>' + e[t].property_type + '</p></div><div><img src="img/estrela.png" width="14" heigth="14">  4,95</div></div><p class="nome">' + e[t].name + "</p><p class=preco><strong>R$ " + e[t].price + "</strong>/noite</p></div>";
            document.querySelector(".quartos").insertAdjacentHTML("beforeend", o) } };
quartosAirBnb(), document.getElementById("linguagem-moeda").onclick = (() => { dropdown() });